export default function Lunch() {
  const [expanded, setExpanded] = React.useState(Array(recipes.length).fill(false)); // State for managing expanded sections

  const handleExpandClick = (index) => {
    const newExpanded = [...expanded];
    newExpanded[index] = !newExpanded[index];
    setExpanded(newExpanded);
  };

  return (
    <div style={{ display: 'flex', flexWrap: 'wrap', gap: '16px', padding: '16px' }}>
      {recipes.map((recipe, index) => (
        <Card key={recipe.id} sx={{ maxWidth: 345 }}>
          <CardHeader
            avatar={
              <Avatar sx={{ bgcolor: red[500] }} aria-label="recipe">
                {recipe.title.charAt(0)}
              </Avatar>
            }
            action={
              <IconButton aria-label="settings">
                <MoreVertIcon />
              </IconButton>
            }
            title={recipe.title}
            subheader={recipe.date}
          />
          <CardMedia
            component="img"
            height="194"
            image={recipe.image}
            alt={recipe.title}
          />
          <CardContent>
            <Typography variant="body2" sx={{ color: 'text.secondary' }}>
              {recipe.description}
            </Typography>
          </CardContent>
          <CardActions disableSpacing>
            <IconButton aria-label="add to favorites">
              <FavoriteIcon />
            </IconButton>
            <IconButton aria-label="share">
              <ShareIcon />
            </IconButton>
            <Button
              onClick={() => handleExpandClick(index)} // Toggle expand/collapse
              aria-expanded={expanded[index]}
              aria-label="show method"
              endIcon={<ExpandMoreIcon />}
              sx={{
                marginLeft: 'auto',
                textTransform: 'none',
                color: 'primary.main',
                fontWeight: 'bold',
                backgroundColor: '#f5f5f5',
                border: '1px solid #ddd',
                padding: '6px 12px',
                '&:hover': {
                  backgroundColor: '#e0e0e0',
                },
              }}
            >
              {expanded[index] ? 'Show Less' : 'Show Method'}
            </Button>
          </CardActions>
          <Collapse in={expanded[index]} timeout="auto" unmountOnExit>
            <CardContent>
              <Typography variant="h6" sx={{ marginBottom: 2 }}>Method:</Typography>
              <Typography>
                {recipe.method.map((step, stepIndex) => (
                  <div key={stepIndex}>{stepIndex + 1}. {step}</div>
                ))}
              </Typography>
            </CardContent>
          </Collapse>
        </Card>
      ))}
    </div>
  );
}
