import * as React from 'react';
import Card from '@mui/material/Card';
import CardHeader from '@mui/material/CardHeader';
import CardMedia from '@mui/material/CardMedia';
import CardContent from '@mui/material/CardContent';
import CardActions from '@mui/material/CardActions';
import Avatar from '@mui/material/Avatar';
import IconButton from '@mui/material/IconButton';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import Collapse from '@mui/material/Collapse';
import { red } from '@mui/material/colors';
import FavoriteIcon from '@mui/icons-material/Favorite';
import ShareIcon from '@mui/icons-material/Share';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import MoreVertIcon from '@mui/icons-material/MoreVert';

const recipes = [
  {
    id: 1,
    title: "Microwave Mug Omelette",
    image: "data:image/jpeg;base64,", // Replace with a real image URL
    description: "A quick and easy omelette cooked in a mug in the microwave.",
    method: [
      "In a microwave-safe mug, whisk together 2 eggs, salt, and pepper.",
      "Add chopped vegetables, cheese, and cooked meat if desired.",
      "Microwave on high for 1-2 minutes until the eggs are set.",
      "Stir and enjoy your quick mug omelette!"
    ],
  },
  {
    id: 2,
    title: "Instant Ramen Noodles",
    image: "data:image/jpeg;base64,", // Replace with a real image URL
    description: "Classic instant ramen noodles cooked to perfection.",
    method: [
      "Boil water in a pot.",
      "Add the ramen noodles and cook for 3-4 minutes.",
      "Stir in the seasoning packet and any extra toppings like green onions or eggs.",
      "Serve hot."
    ],
  },
  {
    id: 3,
    title: "Quick Quesadilla",
    image: "data:image/jpeg;base64,", // Replace with a real image URL
    description: "A cheesy quesadilla made in just a few minutes.",
    method: [
      "Heat a tortilla in a skillet over medium heat.",
      "Sprinkle cheese on one half and fold the tortilla over.",
      "Cook until the cheese melts, about 2 minutes per side.",
      "Cut into wedges and serve with salsa."
    ],
  },
  {
    id: 4,
    title: "Caprese Salad",
    image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQhFAHjd9FV3XOimKWkLTQcFBgu1OwRlEIUTw&s", // Replace with a real image URL
    description: "A fresh salad with tomatoes, mozzarella, and basil.",
    method: [
      "Slice fresh tomatoes and mozzarella cheese.",
      "Layer the tomatoes and cheese with fresh basil leaves.",
      "Drizzle with olive oil and balsamic vinegar.",
      "Season with salt and pepper to taste."
    ],
  }
];

export default function QuickMeal() {
  const [expanded, setExpanded] = React.useState(Array(recipes.length).fill(false)); // State for managing expanded sections

  const handleExpandClick = (index) => {
    const newExpanded = [...expanded];
    newExpanded[index] = !newExpanded[index];
    setExpanded(newExpanded);
  };

  return (
    <div style={{ display: 'flex', flexWrap: 'wrap', gap: '16px', padding: '16px' }}>
      {recipes.map((recipe, index) => (
        <Card key={recipe.id} sx={{ maxWidth: 345 }}>
          <CardHeader
            avatar={
              <Avatar sx={{ bgcolor: red[500] }} aria-label="recipe">
                {recipe.title.charAt(0)}
              </Avatar>
            }
            action={
              <IconButton aria-label="settings">
                <MoreVertIcon />
              </IconButton>
            }
            title={recipe.title}
          />
          <CardMedia
            component="img"
            height="194"
            image={recipe.image}
            alt={recipe.title}
          />
          <CardContent>
            <Typography variant="body2" sx={{ color: 'text.secondary' }}>
              {recipe.description}
            </Typography>
          </CardContent>
          <CardActions disableSpacing>
            <IconButton aria-label="add to favorites">
              <FavoriteIcon />
            </IconButton>
            <IconButton aria-label="share">
              <ShareIcon />
            </IconButton>
            <Button
              onClick={() => handleExpandClick(index)} // Toggle expand/collapse
              aria-expanded={expanded[index]}
              aria-label="show method"
              endIcon={<ExpandMoreIcon />}
              sx={{
                marginLeft: 'auto',
                textTransform: 'none',
                color: 'primary.main',
                fontWeight: 'bold',
                backgroundColor: '#f5f5f5',
                border: '1px solid #ddd',
                padding: '6px 12px',
                '&:hover': {
                  backgroundColor: '#e0e0e0',
                },
              }}
            >
              {expanded[index] ? 'Show Less' : 'Show Method'}
            </Button>
          </CardActions>
          <Collapse in={expanded[index]} timeout="auto" unmountOnExit>
            <CardContent>
              <Typography variant="h6" sx={{ marginBottom: 2 }}>Method:</Typography>
              <Typography>
                {recipe.method.map((step, stepIndex) => (
                  <div key={stepIndex}>{stepIndex + 1}. {step}</div>
                ))}
              </Typography>
            </CardContent>
          </Collapse>
        </Card>
      ))}
    </div>
  );
}
